import React, { useEffect, useRef } from "react";

const AnimatedWaveSection = () => {
  const containerRef = useRef(null);

  useEffect(() => {
    const container = containerRef.current;
    const coins = Array.from(container.querySelectorAll(".coin"));

    const wavePath = container.querySelector("path");

    const getWavePoints = (path, count) => {
      const length = path.getTotalLength();
      const step = length / count;
      const points = [];

      for (let i = 0; i < count; i++) {
        const point = path.getPointAtLength(i * step);
        points.push(point);
      }

      return points;
    };

    const animateCoins = () => {
      const points = getWavePoints(wavePath, coins.length);

      coins.forEach((coin, index) => {
        const point = points[index];
        const offsetY = window.innerWidth < 768 ? 60 : 20; // mobile-friendly adjustment
        coin.style.transform = `translate(${point.x}px, ${point.y - offsetY}px)`;
      });
    };

    animateCoins();
    window.addEventListener("resize", animateCoins);

    return () => {
      window.removeEventListener("resize", animateCoins);
    };
  }, []);

  return (
    <div
      ref={containerRef}
      className="relative w-screen h-[60vh] sm:h-[70vh] md:h-[50vh] overflow-hidden"
      style={{
        background: "linear-gradient(to bottom, #4F46E5 0%, #F3F4F6 100%)",
      }}
    >
      <svg
        className="absolute bottom-0 left-0 w-full h-32 md:h-40"
        viewBox="0 0 1440 320"
        preserveAspectRatio="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          fill="#ffffff"
          d="M0,192L60,170.7C120,149,240,107,360,117.3C480,128,600,192,720,218.7C840,245,960,235,1080,218.7C1200,203,1320,181,1380,170.7L1440,160L1440,320L1380,320C1320,320,1200,320,1080,320C960,320,840,320,720,320C600,320,480,320,360,320C240,320,120,320,60,320L0,320Z"
        />
      </svg>

      {/* ðŸª™ Example coins */}
      <div
        className="coin absolute w-8 h-8 md:w-10 md:h-10 rounded-full bg-yellow-400 shadow-lg"
        style={{ transition: "transform 0.6s ease-in-out" }}
      />
      <div
        className="coin absolute w-8 h-8 md:w-10 md:h-10 rounded-full bg-pink-400 shadow-lg"
        style={{ transition: "transform 0.6s ease-in-out" }}
      />
      <div
        className="coin absolute w-8 h-8 md:w-10 md:h-10 rounded-full bg-blue-400 shadow-lg"
        style={{ transition: "transform 0.6s ease-in-out" }}
      />
      <div
        className="coin absolute w-8 h-8 md:w-10 md:h-10 rounded-full bg-green-400 shadow-lg"
        style={{ transition: "transform 0.6s ease-in-out" }}
      />
      <div
        className="coin absolute w-8 h-8 md:w-10 md:h-10 rounded-full bg-red-400 shadow-lg"
        style={{ transition: "transform 0.6s ease-in-out" }}
      />
    </div>
  );
};

export default AnimatedWaveSection;
